function getFullYear() {

  let date = new Date()

   let year = date.getFullYear()
  return (
    <div>
      <h1>{year}</h1>
    </div>
  )
}

export default getFullYear



export  function getFooterCopy(isIndex) {

  if (isIndex) {
    return <h1>Holberton School</h1>
  }else {
    return <h1>Holberton School main dashboard</h1>
  }
}
